package dataTypes;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number = 12;
		number = 13;
		
		String city = "Krakow";
		city = "Londra";
		
		byte bNumber = 127;
		bNumber = 118;
		
		double dNumber = 12.5;
		dNumber = 13.58;
		
		char character = 'D';
		character = 'F';
		
		boolean isTrue = true;
		isTrue = false;
    
		System.out.println("int: " + number);
		System.out.println("String: " + city);
		System.out.println("byte: " + bNumber);
		System.out.println("double: " + dNumber);
		System.out.println("char: " + character);
		System.out.println("boolean: " + isTrue);
	}

}
